import type { ApiResult } from "@api/Response"
import useAuthContext from "./useAuthContext"
import type { AuthResponseDto } from "../dto"
import { AuthService } from "../services"

const useAuthRefresh = () => {
  const { track, setUser } = useAuthContext()

  const handle = () =>
    track<ApiResult<AuthResponseDto>>(
      AuthService.refresh().then((result) => {
        if (result.ok) {
          setUser(result.data.user)
        } else {
          setUser(null)
        }
        return result
      })
    )
  return handle
}
export default useAuthRefresh
